arguments.argument:
var xsize, ysize;

if hidden exit;

xsize = sprite_width;
ysize = sprite_height;

x = view_xview[0] + 45;
y = view_yview[0] + view_hview[0]-sprite_height-85;

draw_set_valign(fa_top);
draw_set_halign(fa_left);
draw_set_color(c_white);

if isTyping
{
    image_alpha = 1;
}
else
{
    image_alpha = max(alarm[1]/30, 0.2);
}

// Draw the background
draw_sprite(sprite_index, 0, x, y);

// If we're typing something, draw that
if isTyping
{
    var str, temp;
    if blinking_underscore
    {
        temp = keyboard_string+"_";
    }
    else
    {
        temp = keyboard_string;
    }
    str = temp
    draw_set_alpha(1)
    draw_text(x+60, y+ysize-13, str);
}

