var message;
for(i=0; i<ds_list_size(chatLog); i+=1)
{
    message = ds_list_find_value(chatLog, ds_list_size(chatLog)-1-i)

    switch string_copy(message, 0, 3)
    {
        case "/:/":// If a color-code was included.
            draw_set_color(getColorCode(string_copy(message, 4, 6)))
            draw_text(x+10, y+ysize-45-12*(ds_list_size(chatLog)-i), string_copy(message, 5, string_length(message)));
            break

        default:
            draw_set_color(c_white);
            draw_text(x+10, y+ysize-45-12*(ds_list_size(chatLog)-i), message);
            //draw_text(x+offset+10, y+ysize-45-12*(ds_list_size(chatLog)-i), message);
            break
    }
}
draw_set_color(c_white);
