arguments.argument:
goto = 0;
if(global.enableMetaUI)
{
    if (global.chatbox.isTyping)
    {
        isTyping = false;
        keyboard_string = "";
        alarm[0] = -1
        blinking_underscore = false;
    }
    else
        goto = 1;
}
if(goto or !global.enableMetaUI)
{
    if(!instance_exists(InGameMenuController) and !instance_exists(OptionsController)
    and !instance_exists(ControlsController)) and !instance_exists(HUDOptionsController)
    {
        instance_create(x,y,InGameMenuController);
    }
}
