arguments.argument:
x = view_xview[0];
y = view_yview[0] + view_hview[0] - 12;

draw_set_color(c_white);
draw_set_valign(fa_top);
draw_set_halign(fa_left);
draw_set_alpha(1);
draw_surface(surface, x, y);

var listlen, listheight;
listlen = ds_list_size(global.ConsoleLog);
listheight = string_height("_") * listlen;

var i;
draw_set_color(c_orange);
for (i = 0; i < listlen; i += 1) {
    draw_text(x, y - listheight + string_height("_") * i, ds_list_find_value(global.ConsoleLog, i));
}
