arguments.argument:
x = view_xview[0] + global.ConsoleWindowX;
y = view_yview[0] + global.ConsoleWindowY;

if (x <= mouse_x && mouse_x < x + console_width && y - console_height <= mouse_y && mouse_y < y) {
    dragging = true;
    dragstart_mousex = mouse_x;
    dragstart_mousey = mouse_y;
    dragstart_winx = global.ConsoleWindowX;
    dragstart_winy = global.ConsoleWindowY;
}
