arguments.argument:
{
    cursor_sprite = CrosshairS;
    global.levelchoice = 1

    canEsc = false;
    alarm[1] = 15;

    menu_create(self, 40, 300, 200, 200, 30);
    menu_setdimmed(self);

    menu_addlink(self, "Options", '
        instance_destroy();
        instance_create(0,0,OptionsController);
    ');
    menu_addlink(self, "Return to Game", '
        instance_destroy();
    ');
    menu_addlink(self, "", '');
    menu_addlink(self, "Disconnect", '
        // Force dedicated mode to off so you can go to main menu instead of just restarting server
        global.dedicatedMode = 0;
        with(Client)
            instance_destroy();

        with(GameServer)
            instance_destroy();
    ');
    menu_addlink(self, "Quit Game", '
        game_end();
    ');
}
