    menu_create(40, 300, 200, 200, 30);
    menu_setdimmed();
    
    menu_addlink("Options", '
        instance_destroy();
        instance_create(0,0,OptionsController);
    ');
    menu_addback("Return to Game", '
        instance_destroy();
    ');
    switch (global.consoleOpen)
    {
        case 0:
            menu_addlink("Open Console", '
                instance_create(x, y, Console)
                menu_rename(3, "Close Console")
            ');
            break;
        case 1:
            menu_addlink("Close Console", '
                with Console {instance_destroy()}
                menu_rename(3, "Open Console")
            ');
    }
    menu_addlink("Disconnect", '
        // Force dedicated mode to off so you can go to main menu instead of just restarting server
        global.dedicatedMode = 0;
        with(Client)
            instance_destroy();
            
        with(GameServer)
            instance_destroy();
    ');
    menu_addlink("Quit Game", '
        game_end();
    ');
