arguments.argument:
if editing != -1 { 
    switch (item_type[editing]) {
        case "edittext":
            execute_string(item_script[editing], item_value[editing]);
            break;
        case "edittext2":
            item_value[editing] = execute_string(item_script[editing], item_value[editing]);
            break;
        case "editnum":
            execute_string(item_script[editing], floor(real(item_value[editing])));
            break;
    }
    editing = -1;
}
