arguments.argument:
if (ammoCount < maxAmmo)
{
    ammoCount = maxAmmo;
    if (global.particles == PARTICLES_NORMAL && image_alpha > 0.1)
    {
        repeat(6)
        {
            var shell;
            shell = instance_create(x + lengthdir_x(13, owner.aimDirection), y + lengthdir_y(13, owner.aimDirection), Shell);
            shell.direction = owner.aimDirection + (70 - random(80)) * image_xscale;
            shell.speed *= 0.7;
            shell.sprite_index = MinigunShellS;
        }
    }
}
